import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
      main: '#ffb5a7', // Soft peach for primary actions
    },
    secondary: {
      main: '#b0c4b1', // Muted green for secondary elements
    },
    background: {
      default: '#dedbd2', // Light gray for the background
      paper: '#ffffff',
    },
    text: {
      primary: '#4a5759', // Dark slate for text
      secondary: '#4a5759',
    },
  },
});

export default theme;
