import React, { useEffect, useState } from 'react';
import Select from 'react-select';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from 'recharts';

// Helper function to format the tooltip's value
const formatTooltipValue = (value, name, props) => {
  const category = props.payload.Category;
  const categoriesWithDollarSign = [
    'Median household income (in 2022 dollars)',
    'Median value of owner-occupied housing units, 2018-2022',
    'Median selected monthly owner costs -with a mortgage, 2018-2022',
    'Median selected monthly owner costs -without a mortgage, 2018-2022',
    'Median gross rent, 2018-2022',
    // Add more categories as needed
  ];

  if (categoriesWithDollarSign.includes(category)) {
    // Format the value with a dollar sign and commas
    return `$${Number(value).toLocaleString()}`;
  }

  // Handle non-monetary values
  return value.toLocaleString();
};

function DataVisualization() {
  const [data, setData] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/data')
      .then((response) => response.json())
      .then((data) => {
        // Update to handle parsing of all values correctly
        const parsedData = data.map((item) => ({
          ...item,
          Value: item.Category.includes('Median')
            ? item.Value
            : parseFloat(item.Value.replace(/[$,]/g, '')),
        }));
        setData(parsedData);

        const categoryOptions = Array.from(
          new Set(parsedData.map((item) => item.Category))
        ).map((category) => ({ value: category, label: category }));
        setCategories(categoryOptions);
        if (categoryOptions.length > 0) {
          setSelectedCategory(categoryOptions[0].value); // Default to first category
        }
      })
      .catch((error) => console.error('Error:', error));
  }, []);

  const filteredData = selectedCategory
    ? data.filter((item) => item.Category === selectedCategory)
    : [];

  return (
    <div>
      <Select
        options={categories}
        onChange={(selectedOption) => setSelectedCategory(selectedOption.value)}
        value={categories.find((option) => option.value === selectedCategory)}
      />
      <ResponsiveContainer width="100%" height={400}>
        <LineChart
          data={filteredData}
          margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="City" />
          <YAxis />
          <Tooltip
            formatter={(value, name, props) =>
              formatTooltipValue(value, name, props)
            }
          />
          <Legend />
          <Line
            type="monotone"
            dataKey="Value"
            stroke="#8884d8"
            strokeWidth={2}
            dot={{ stroke: '#8884d8', strokeWidth: 2, fill: '#ffffff' }}
            activeDot={{ r: 8, fill: '#8884d8' }}
            animationDuration={3000}
          />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
}

export default DataVisualization;
