import React, { useState, useEffect } from 'react';
import {
  AreaChart,
  Area,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from 'recharts';

const PopulationGrowthChart = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/data')
      .then((response) => response.json())
      .then((fetchedData) => {
        const cities = [
          'Kailua, HI',
          'Columbia, MO',
          'Charlotte, NC',
          'Portland, OR',
          'Seattle, WA',
        ];
        const years = {
          'Population Estimates as of 2022': '2022 Population',
          'Population, Census, 2020': '2020 Population',
          'Population, Census, 2010': '2010 Population',
        };
        const processedData = cities.map((city) => {
          let cityData = { City: city };
          Object.entries(years).forEach(([yearKey, chartKey]) => {
            const yearData = fetchedData.find(
              (d) => d.Category === yearKey && d.City === city
            );
            cityData[chartKey] = yearData
              ? parseFloat(yearData.Value.replace(/,/g, ''))
              : 0;
          });
          return cityData;
        });
        console.log(processedData);
        setData(processedData);
      })
      .catch((error) => console.error('Error fetching data:', error));
  }, []);

  if (data.length === 0) {
    return <div>Loading...</div>;
  }

  return (
    <ResponsiveContainer width="100%" height={400}>
      <AreaChart
        data={data}
        margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
      >
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="City" />
        <YAxis />
        <Tooltip />
        <Legend />
        <Area
          type="monotone"
          dataKey="2010 Population"
          stroke="#8884d8"
          fill="#8884d8"
          fillOpacity={0.7}
          activeDot={{ r: 8 }}
        />
        <Area
          type="monotone"
          dataKey="2020 Population"
          stroke="#82ca9d"
          fill="#82ca9d"
          fillOpacity={0.5}
          activeDot={{ r: 8 }}
        />
        <Area
          type="monotone"
          dataKey="2022 Population"
          stroke="#ffc658"
          fill="#ffc658"
          fillOpacity={0.3}
          activeDot={{ r: 8 }}
        />
      </AreaChart>
    </ResponsiveContainer>
  );
};

export default PopulationGrowthChart;
