import React, { useEffect, useState } from 'react';
import { PieChart, Pie, Tooltip, Cell, ResponsiveContainer } from 'recharts';

const CityDemographicsPieChart = ({ cityData }) => {
  // Updated color palette for better visibility on a white background
  const COLORS = ['#ff6b6b', '#4ecdc4', '#ff9f1c', '#2ec4b6'];
  const demographicsData = [
    { name: 'White population', value: cityData['White Population alone'] },
    {
      name: 'Native Hawaiian and Pacific Islander',
      value:
        cityData['Native Hawaiian and Other Pacific Islander Population alone'],
    },
    {
      name: 'Two or More Races',
      value: cityData['Population of Two or More Races'],
    },
    {
      name: 'Hispanic or Latino',
      value: cityData['Hispanic or Latino Population'],
    },
  ];

  return (
    <ResponsiveContainer width="100%" height={200}>
      <PieChart>
        <Pie
          data={demographicsData}
          dataKey="value"
          nameKey="name"
          cx="50%"
          cy="50%"
          outerRadius={67}
          label={({ name, percent }) =>
            `${name}: ${(percent * 100).toFixed(1)}%`
          }
        >
          {demographicsData.map((entry, index) => (
            <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
          ))}
        </Pie>
        <Tooltip />
      </PieChart>
    </ResponsiveContainer>
  );
};

const DemographicsPieCharts = ({ data }) => {
  const [citiesData, setCitiesData] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/data')
      .then((response) => response.json())
      .then((jsonData) => setCitiesData(jsonData))
      .catch((error) => console.error('Error fetching data: ', error));
  }, []);

  return (
    <div>
      <h2
        style={{ textAlign: 'center', marginBottom: '10px', color: '#6d6875' }}
      >
        City Demographics Overview
      </h2>
      <div
        style={{
          display: 'flex',
          justifyContent: 'space-around',
          alignItems: 'flex-start',
        }}
      >
        {/* Legend */}
        <div style={{ width: '15%', textAlign: 'left', padding: '8px' }}>
          <h3 style={{ color: '#6d6875' }}>Legend</h3>
          <p>
            <span style={{ color: '#ff6b6b' }}>■ White</span>
          </p>
          <p>
            <span style={{ color: '#4ecdc4' }}>
              ■ Native Hawaiian and Pacific Islander
            </span>
          </p>
          <p>
            <span style={{ color: '#ff9f1c' }}>■ Two or More Races</span>
          </p>
          <p>
            <span style={{ color: '#2ec4b6' }}>■ Hispanic or Latino</span>
          </p>
        </div>
        {/* Pie Charts */}
        <div
          style={{
            display: 'flex',
            flexWrap: 'wrap',
            justifyContent: 'center',
            width: '68%',
          }}
        >
          {citiesData.map((cityData) => (
            <div
              key={cityData.City}
              style={{ width: '48%', padding: '8px', boxSizing: 'border-box' }}
            >
              <h3 style={{ textAlign: 'center', color: '#6d6875' }}>
                {cityData.City}
              </h3>
              <CityDemographicsPieChart cityData={cityData} />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default DemographicsPieCharts;
