import React, { useState, useEffect } from 'react';
import {
  ComposedChart,
  Line,
  Area,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from 'recharts';

const PopulationGrowthChart = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    // Adjust the URL to where your backend endpoint is hosted
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:5000/data');
        const jsonData = await response.json();
        setData(jsonData);
      } catch (error) {
        console.error('Error fetching data: ', error);
      }
    };

    fetchData();
  }, []);

  // No need for transformation in this example; using the data as is
  // If your data needs transformation or aggregation, do it here

  return (
    <ResponsiveContainer width="100%" height={400}>
      <ComposedChart
        data={data}
        margin={{
          top: 20,
          right: 20,
          bottom: 20,
          left: 20,
        }}
      >
        <CartesianGrid stroke="#f5f5f5" />
        <XAxis dataKey="City" />
        <YAxis />
        <Tooltip />
        <Legend />
        <Bar dataKey="Population" barSize={20} fill="#413ea0" />
        <Line type="monotone" dataKey="Area" stroke="#ff7300" />
        <Area
          type="monotone"
          dataKey="Pollution"
          fill="#8884d8"
          stroke="#8884d8"
        />
      </ComposedChart>
    </ResponsiveContainer>
  );
};

export default PopulationGrowthChart;
