import React, { useEffect, useState } from 'react';
import {
  PieChart,
  Pie,
  Tooltip,
  Cell,
  ResponsiveContainer,
  Legend,
} from 'recharts';

const CityDemographicsPieChart = ({ cityData }) => {
  const COLORS = ['#ffb5a7', '#b8c0a2', '#fde2e4', '#e2ece9']; // Adjusted colors to your theme
  const demographicsData = [
    { name: 'White population', value: cityData['White population alone'] },
    {
      name: 'Native Hawaiian and Pacific Islander',
      value:
        cityData['Native Hawaiian and Other Pacific Islander population alone'],
    },
    {
      name: 'Two or More Races',
      value: cityData['Population of Two or More Races'],
    },
    {
      name: 'Hispanic or Latino',
      value: cityData['Hispanic or Latino population'],
    },
  ];

  return (
    <ResponsiveContainer width="100%" height={250}>
      <PieChart>
        <Pie
          data={demographicsData}
          dataKey="value"
          nameKey="name"
          cx="50%"
          cy="50%"
          outerRadius={85}
          fill="#8884d8"
          label
        >
          {demographicsData.map((entry, index) => (
            <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
          ))}
        </Pie>
        <Tooltip />
      </PieChart>
    </ResponsiveContainer>
  );
};

// Custom Legend Component
const renderColorfulLegendText = (value, entry) => {
  const { color } = entry;
  return <span style={{ color }}>{value}</span>;
};

const DemographicsPieCharts = ({ data }) => {
  const [citiesData, setCitiesData] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/data')
      .then((response) => response.json())
      .then((jsonData) => setCitiesData(jsonData))
      .catch((error) => console.error('Error fetching data: ', error));
  }, []);

  // Data for the unified legend
  const legendData = [
    { value: 'White population', type: 'square', color: '#ffb5a7' },
    {
      value: 'Native Hawaiian and Pacific Islander',
      type: 'square',
      color: '#b8c0a2',
    },
    { value: 'Two or More Races', type: 'square', color: '#fde2e4' },
    { value: 'Hispanic or Latino', type: 'square', color: '#e2ece9' },
  ];

  return (
    <div>
      <h2 style={{ textAlign: 'center', color: '#6d6875' }}>
        City Demographics Overview
      </h2>
      <div
        style={{
          display: 'flex',
          justifyContent: 'center',
          marginBottom: '20px',
        }}
      >
        <Legend payload={legendData} formatter={renderColorfulLegendText} />
      </div>
      <div
        style={{
          display: 'flex',
          flexDirection: 'row',
          flexWrap: 'wrap',
          justifyContent: 'space-around',
          alignItems: 'top',
        }}
      >
        {citiesData.map((cityData) => (
          <div
            key={cityData.City}
            style={{
              width: '30%',
              margin: '10px',
              padding: '10px',
              boxSizing: 'border-box',
            }}
          >
            <h3 style={{ textAlign: 'center', color: '#6d6875' }}>
              {cityData.City}
            </h3>
            <CityDemographicsPieChart cityData={cityData} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default DemographicsPieCharts;
