import React, { useEffect, useState } from 'react';
import Select from 'react-select';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from 'recharts';

const monetaryCategories = [
  'Median value of owner-occupied housing units',
  'Median selected monthly owner costs with a mortgage',
  'Median gross rent',
  'Per capita income in past 12 months (2022)',
  'Annual 10th percentile wage',
  'Annual median wage',
  'Annual 90th percentile wage',
];

const percentageCategories = [
  'Population change from 2020 to 2022',
  'People under 5 years old',
  'People under 18 years old',
  'People 65 years and over',
  'Women population',
  'White population alone',
  'Native Hawaiian and Other Pacific Islander population alone',
  'Population of Two or More Races',
  'Hispanic or Latino population',
  "Bachelor's degree or higher",
  'In civilian labor force, total population age 16+ years',
  'In civilian labor force, female population age 16+ years',
  'Persons in poverty',
];

// Update this list with new categories based on the transformed dataset
const categories = [
  ...monetaryCategories,
  ...percentageCategories,
];

function DataVisualization() {
  const [data, setData] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(categories[0]);
  const [chartData, setChartData] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/data')
      .then((response) => response.json())
      .then((fetchedData) => {
        setData(fetchedData);
      })
      .catch((error) => console.error('Error fetching data:', error));
  }, []);

  useEffect(() => {
    const preparedData = data.map((item) => ({
      City: item.City,
      Value:
        monetaryCategories.includes(selectedCategory) ||
        percentageCategories.includes(selectedCategory)
          ? parseFloat(item[selectedCategory].replace(/[^0-9.-]+/g, ''))
          : item[selectedCategory],
    }));
    setChartData(preparedData);
  }, [selectedCategory, data]);

  const formatTooltipValue = (value, name, props) => {
    if (monetaryCategories.includes(selectedCategory)) {
      return `$${value.toLocaleString()}`;
    } else if (percentageCategories.includes(selectedCategory)) {
      return `${value}%`;
    }
    return value.toLocaleString();
  };

  const categoryOptions = categories.map((category) => ({
    value: category,
    label: category,
  }));

  return (
    <div>
      <Select
        options={categoryOptions}
        onChange={(option) => setSelectedCategory(option.value)}
        value={{ value: selectedCategory, label: selectedCategory }}
      />
      <ResponsiveContainer width="100%" height={400}>
        <LineChart
          data={chartData}
          margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="City" />
          <YAxis />
          <Tooltip formatter={formatTooltipValue} />
          <Legend />
          <Line
            type="monotone"
            dataKey="Value"
            name={selectedCategory} // Ensure the legend uses the selected category name
            stroke="#8884d8"
            activeDot={{ r: 8 }}
          />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
}

export default DataVisualization;
