import React, { useEffect, useState } from 'react';
import Select from 'react-select';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from 'recharts';

// Constants for monetary and percentage categories
const monetaryCategories = [
  'Median value of owner-occupied housing units',
  'Median selected monthly owner costs with a mortgage',
  'Median gross rent',
  'Per capita income in past 12 months (2022)',
  'Annual 10th percentile wage',
  'Annual median wage',
  'Annual 90th percentile wage',
];

const percentageCategories = [
  'Population change from 2020 to 2022',
  'People under 5 years old',
  'People under 18 years old',
  'People 65 years and over',
  'Women population',
  'White population alone',
  'Native Hawaiian and Other Pacific Islander population alone',
  'Population of Two or More Races',
  'Hispanic or Latino population',
  "Bachelor's degree or higher",
  'In civilian labor force, total population age 16+ years',
  'In civilian labor force, female population age 16+ years',
  'Persons in poverty',
];

function DataVisualization() {
  // State for data, selected category, and category options
  const [data, setData] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/data')
      .then((response) => response.json())
      .then((data) => {
        // Parse data and convert strings to appropriate numeric values
        const parsedData = data.map((item) => {
          let value = item.Value;
          if (percentageCategories.includes(item.Category)) {
            value = parseFloat(value.replace(/[^0-9.]/g, ''));
          } else if (monetaryCategories.includes(item.Category)) {
            value = parseFloat(value.replace(/[$,]/g, ''));
          }
          return { ...item, Value: value };
        });

        setData(parsedData);

        // Extract unique categories for the Select component
        const categoryOptions = Array.from(
          new Set(parsedData.map((item) => item.Category))
        ).map((Category) => ({ value: Category, label: Category }));

        setCategories(categoryOptions);

        // Automatically select the first category if available
        if (categoryOptions.length > 0) {
          setSelectedCategory(categoryOptions[0].value);
        }
      })
      .catch((error) => console.error('Error:', error));
  }, []);

  // Function to format tooltip values
  const formatTooltipValue = (value, name, props) => {
    const category = props.payload.Category;
    if (monetaryCategories.includes(category)) {
      return `$${Number(value).toLocaleString()}`;
    } else if (percentageCategories.includes(category)) {
      return `${value}%`;
    }
    return value.toLocaleString();
  };

  // Filter data for the selected category
  const filteredData = data.filter(
    (item) => item.Category === selectedCategory
  );

  return (
    <div>
      <Select
        options={categories}
        onChange={(selectedOption) => setSelectedCategory(selectedOption.value)}
        value={categories.find((option) => option.value === selectedCategory)}
      />
      <ResponsiveContainer width="100%" height={400}>
        <LineChart
          data={filteredData}
          margin={{ top: 5, right: 30, left: 20, bottom: 5 }}
        >
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="City" />
          <YAxis />
          <Tooltip formatter={formatTooltipValue} />
          <Legend />
          <Line
            type="monotone"
            dataKey="Value"
            stroke="#8884d8"
            strokeWidth={2}
            dot={{ stroke: '#8884d8', strokeWidth: 2, fill: '#ffffff' }}
            activeDot={{ r: 8, fill: '#8884d8' }}
            animationDuration={3000}
          />
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
}

export default DataVisualization;
